<?phpclass Dbg {    /**     * EGMC 20150629     * Imprime datos entre tag pre     * <pre>$data</pre>      * aplica die(); para finalizar     * @param string $data     */    public static function pd($data = null, $limit = 2) {        self::_data($data, $limit);        die;    }    /**     * EGMC 20150629     * Imprime datos entre tag pre     * <pre>$data</pre>     * @param array $data     * @param int $limit     */    public static function data($data, $limit = 2) {        self::_data($data, $limit);    }    /**     * EGMC 20150629     * Imprime datos entre tag pre     * <pre>$data</pre>     * @param mixed $data     */    private static function _data($data = null, $limit = 2) {                $backtace = debug_backtrace(DEBUG_BACKTRACE_IGNORE_ARGS, $limit);        $backtace = array_pop($backtace);//        echo '<pre style="background-color:#F5F5F5; border:solid 1px #E3E3E3; ">';        echo '<pre class="alert alert-warning" role="alert">';        if (!empty($backtace)) {            echo '<strong>Line (' . $backtace['line'] . '): ' . $backtace['file'] . '</strong><br>';        } else {            echo 'NO LINE: NO FILE <br>';        }        if ($data != null) {            print_r($data);        }        echo '</pre>';    }    /**     * EGMC 20150727     * Imprime arreglo de querys ejecutados     * @param bolean $delete inicializa la variable de sesi√≥n     */    public static function printQuerys($delete = true) {        self::_data($_SESSION['DB']['Execute']);        if ($delete) {            $_SESSION['DB']['Execute'] = array();        }    }    /**     * EGMC 20150629     * Print Memory Usage     */    public static function pmu() {        self::_data(date('Y-m-d H:i:s') . " Peak memory usage: " . (memory_get_peak_usage(true) / 1024 / 1024) . " MB");    }    /**     * EGMC 20150629     * Imprime tabla con datos de la variable $_SERVER     */    public function dataServer() {        $indicesServer = array('PHP_SELF',            'argv',            'argc',            'GATEWAY_INTERFACE',            'SERVER_ADDR',            'SERVER_NAME',            'SERVER_SOFTWARE',            'SERVER_PROTOCOL',            'REQUEST_METHOD',            'REQUEST_TIME',            'REQUEST_TIME_FLOAT',            'QUERY_STRING',            'DOCUMENT_ROOT',            'HTTP_ACCEPT',            'HTTP_ACCEPT_CHARSET',            'HTTP_ACCEPT_ENCODING',            'HTTP_ACCEPT_LANGUAGE',            'HTTP_CONNECTION',            'HTTP_HOST',            'HTTP_REFERER',            'HTTP_USER_AGENT',            'HTTPS',            'REMOTE_ADDR',            'REMOTE_HOST',            'REMOTE_PORT',            'REMOTE_USER',            'REDIRECT_REMOTE_USER',            'SCRIPT_FILENAME',            'SERVER_ADMIN',            'SERVER_PORT',            'SERVER_SIGNATURE',            'PATH_TRANSLATED',            'SCRIPT_NAME',            'REQUEST_URI',            'PHP_AUTH_DIGEST',            'PHP_AUTH_USER',            'PHP_AUTH_PW',            'AUTH_TYPE',            'PATH_INFO',            'ORIG_PATH_INFO');        echo '<table cellpadding="10">';        foreach ($indicesServer as $arg) {            if (isset($_SERVER[$arg])) {                echo '<tr><td>' . $arg . '</td><td>' . $_SERVER[$arg] . '</td></tr>';            } else {                echo '<tr><td>' . $arg . '</td><td>-</td></tr>';            }        }        echo '</table>';        die;    }}