<?phprequire_once PATH_ADM_VENDOR . '/autoload.php';class PDF {    public $mPDF;    public function mPDF($mode = '', $format = 'A4', $default_font_size = 0, $default_font = '', $mgl = 15, $mgr = 15, $mgt = 16, $mgb = 16, $mgh = 9, $mgf = 9, $orientation = 'P') {        $this->mPDF = new mPDF($mode, $format, $default_font_size, $default_font, $mgl, $mgr, $mgt, $mgb, $mgh, $mgf, $orientation);//        $mpdf = new mPDF('c', 'A4', '', '', 32, 25, 27, 25, 16, 13);        return $this->mPDF;    }    public function mPDFTemplate($html, $mPDF = null) {        if ($mPDF == null) {            $this->mPDF = $this->mPDF();        }        $this->mPDF->SetDisplayMode('fullpage');        $this->mPDF->list_indent_first_level = 0; // 1 or 0 - whether to indent the first level of a list// LOAD a stylesheet        $stylesheet = file_get_contents(PATH_ADM_CSS . '/mPDF/mpdfstyletables.css');        $this->mPDF->WriteHTML($stylesheet, 1); // The parameter 1 tells that this is css/style only and no body/html/text        $this->mPDF->WriteHTML($html, 2);        $this->mPDF->Output('mpdf.pdf', 'I');        exit;    }    /**     * EGMC 20151221     * Genera y almacen PDF     * @param string $html     * @param string $nameFile     * @param string $pathPDF     * @param obj $mPDF     * @return string     */    public function mPDFSave($html, $nameFile = 'pdf', $pathPDF = PATH_ADM_MEDIA, $mPDF = null) {        if ($mPDF == null) {            $this->mPDF = $this->mPDF();        }                /**         * EGMC 20151221         * Asigna configuraciones         */        $this->mPDF->SetDisplayMode('fullpage');        $this->mPDF->list_indent_first_level = 0; // 1 or 0 - whether to indent the first level of a list                /**         * EGMC 20151221         * Carga los estilos de las tablas         */        $stylesheet = file_get_contents(PATH_ADM_CSS . '/mPDF/mpdfstyletables.css');        $this->mPDF->WriteHTML($stylesheet, 1); // The parameter 1 tells that this is css/style only and no body/html/text        /**         * EGMC 20151221         * Carga el html         */        $this->mPDF->WriteHTML($html, 2);        /**         * EGMC 20151221         * Guarda el PDF         */        $nameFile .= '.pdf';        $this->mPDF->Output($pathPDF . DS . $nameFile, 'F');        return $nameFile;    }    public function mPDFDownload($html, $nameFile = 'pdf', $mPDF = null) {        if ($mPDF == null) {            $this->mPDF = $this->mPDF();        }        $this->mPDF->SetDisplayMode('fullpage');        $this->mPDF->list_indent_first_level = 0; // 1 or 0 - whether to indent the first level of a list// LOAD a stylesheet        $stylesheet = file_get_contents(PATH_ADM_CSS . '/mPDF/mpdfstyletables.css');        $this->mPDF->WriteHTML($stylesheet, 1); // The parameter 1 tells that this is css/style only and no body/html/text        $this->mPDF->WriteHTML($html, 2);        $this->mPDF->Output($nameFile . '.pdf', 'D');        exit;    }    public function qrBarcode($content = 'Eddin') {        $barcodeobj = new TCPDF2DBarcode($content, 'QRCODE,H');// output the barcode as PNG image        $barcodeobj->getBarcodePNG(6, 6, array(0, 0, 0));    }}